import React from 'react';
import { useParams } from 'react-router-dom';

const productData = {
  1: { name: 'iPhone 13', price: '$999', description: 'Latest iPhone model with A15 chip.', image: '/assets/iphone13_resized.jpg' },
  2: { name: 'Samsung Galaxy S22', price: '$899', description: 'Flagship Samsung phone with AMOLED display.', image: '/assets/s22_resized.jpg' },
  3: { name: 'Apple Watch', price: '$399', description: 'Smartwatch with health tracking features.', image: '/assets/apple_watch_resized.jpg' },
  4: { name: 'Sony Headphones', price: '$199', description: 'Noise-cancelling over-ear headphones.', image: '/assets/sony_headphones_resized.jpg' }
};

function ProductDetails() {
  const { id } = useParams();
  const product = productData[id];

  return (
    <div>
      <h2>{product.name}</h2>
      <img src={product.image} alt={product.name} />
      <p>{product.price}</p>
      <p>{product.description}</p>
    </div>
  );
}

export default ProductDetails;
