import React from 'react';
import { useParams } from 'react-router-dom';

const productData = {
  1: { name: 'iPhone 13', price: '$999', description: 'Latest iPhone model with A15 chip.', image: 'https://drive.google.com/uc?id=IMAGE_ID' },
  2: { name: 'Samsung Galaxy S22', price: '$899', description: 'Flagship Samsung phone with dynamic AMOLED display.', image: 'https://drive.google.com/uc?id=IMAGE_ID' },
  3: { name: 'Apple Watch', price: '$399', description: 'Smartwatch with health tracking features.', image: 'https://drive.google.com/uc?id=IMAGE_ID' },
  4: { name: 'Sony Headphones', price: '$199', description: 'Noise cancelling over-ear headphones.', image: 'https://drive.google.com/uc?id=IMAGE_ID' }
};

function ProductDetails() {
  const { id } = useParams();
  const product = productData[id];

  if (!product) {
    return <h2>Product not found</h2>;
  }

  return (
    <div className="product-details">
      <img src={product.image} alt={product.name} />
      <h1>{product.name}</h1>
      <p className="price">{product.price}</p>
      <p>{product.description}</p>
    </div>
  );
}

export default ProductDetails;
