import React from 'react';
import ProductCard from './ProductCard';
import { useParams } from 'react-router-dom';

const categoryProducts = {
  smartphones: [
    { id: 1, name: 'iPhone 13', price: '$999', image: 'https://https://imgur.com/Yp3aBW7' },
    { id: 2, name: 'Samsung Galaxy S22', price: '$899', image: 'https://drive.google.com/uc?id=1L4BqygXIieZmE2i30MQIXbBai1SapoM4' }
  ],
  tablets: [
    { id: 3, name: 'iPad Pro', price: '$1099', image: 'https://drive.google.com/uc?id=1JPy-PZAqWncsntEzB_jB54ynzyHNf0E3' }
  ]
};

function CategoryPage() {
  const { categoryName } = useParams();
  const products = categoryProducts[categoryName] || [];

  return (
    <div>
      <h2>{categoryName.charAt(0).toUpperCase() + categoryName.slice(1)}</h2>
      <div className="product-grid">
        {products.map(product => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
}

export default CategoryPage;
